import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-231.8 Powers and duties of the Committee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-231.8.md","filePath":"vacode/30-231.8.md"}'),r={name:"vacode/30-231.8.md"},o=i('<h1 id="§-30-231-8-powers-and-duties-of-the-committee" tabindex="-1">§ 30-231.8 Powers and duties of the Committee. <a class="header-anchor" href="#§-30-231-8-powers-and-duties-of-the-committee" aria-label="Permalink to &quot;§ 30-231.8 Powers and duties of the Committee.&quot;">​</a></h1><p>The Committee shall have the following powers and duties:</p><ol><li><p>Establish criteria for the awarding of scholarships, including, but not limited to, eligibility for and the renewal of scholarships, evidence of satisfactory academic achievement in accordance with § <a href="http://law.lis.virginia.gov/vacode/30-231.2/" target="_blank" rel="noreferrer">30-231.2</a>, terms and conditions of scholarships awarded pursuant to § <a href="http://law.lis.virginia.gov/vacode/30-231.3/" target="_blank" rel="noreferrer">30-231.3</a>, the cancellation, rescindment, and recovery of scholarship awards, and conditions for which repayment of scholarships, or any part thereof, may be required;</p></li><li><p>Evaluate applications for and select recipients of the Brown v. Board of Education scholarships, in accordance with the provisions of this chapter;</p></li><li><p>Establish standards and determine approved education programs to ensure that the Program is implemented and administered in a manner that preserves the purpose for which it was created;</p></li><li><p>Establish, revise as necessary, and implement policies and standards to govern all aspects of the Program;</p></li><li><p>Confer with the Board of Education, Virginia Community College System, State Council of Higher Education, and Private College Advisory Board to the State Council of Higher Education to establish a protocol to facilitate the dual enrollment of eligible students in two-year and four-year degree programs, and the conventional enrollment of such students in public and private two-year and four-year accredited institutions of higher education;</p></li><li><p>Develop and implement a system to provide individualized transition programs and services, including, but not limited to, remediation, acceleration, and fundamental basic life skills, designed to prepare eligible students for academic success in the preparation program for a high school equivalency examination approved by the Board of Education, earning college credit through the College-Level Examination Program (CLEP) examinations, adult basic education programs, two-year, four-year, graduate, and professional degree programs;</p></li><li><p>Determine annually the sum of any gifts, grants, donations, bequests, or other funds in the Brown v. Board of Education Scholarship Program Fund, and set the annual maximum scholarship award, and the maximum number of scholarships that may be awarded each year;</p></li><li><p>Seek, receive, and expend gifts, grants, donations, bequests, or other funds from any source on behalf of the Program for its support and to facilitate its purpose;</p></li><li><p>Make the first awards of the Brown v. Board of Education Scholarship Program to eligible students between July 1, 2004, and July 1, 2006, but no later than July 1, 2006; and</p></li><li><p>Perform such other duties, functions, and activities as may be necessary to facilitate and implement the objectives of this chapter.</p></li></ol><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0753" target="_blank" rel="noreferrer">753</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0680" target="_blank" rel="noreferrer">680</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0444" target="_blank" rel="noreferrer">444</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0579" target="_blank" rel="noreferrer">579</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0084" target="_blank" rel="noreferrer">84</a>.</p>',4),n=[o];function s(l,d,c,h,p,f){return a(),t("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
