import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-235 (Contingent expiration date -- see notes) Compensation and expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-235.md","filePath":"vacode/30-235.md"}'),r={name:"vacode/30-235.md"},i=t('<h1 id="§-30-235-contingent-expiration-date-see-notes-compensation-and-expenses" tabindex="-1">§ 30-235 (Contingent expiration date -- see notes) Compensation and expenses. <a class="header-anchor" href="#§-30-235-contingent-expiration-date-see-notes-compensation-and-expenses" aria-label="Permalink to &quot;§ 30-235 (Contingent expiration date -- see notes) Compensation and expenses.&quot;">​</a></h1><p>Legislative members of the Commission shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative citizen members shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> for the performance of their duties. All members shall be reimbursed for reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. However, all such compensation and expenses shall be paid from existing appropriations to the Commission.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0992" target="_blank" rel="noreferrer">992</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1015" target="_blank" rel="noreferrer">1015</a>.</p>',3),o=[i];function s(l,p,c,d,h,_){return a(),n("div",null,o)}const g=e(r,[["render",s]]);export{f as __pageData,g as default};
