import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-284 Powers and duties of Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-284.md","filePath":"vacode/30-284.md"}'),s={name:"vacode/30-284.md"},o=i('<h1 id="§-30-284-powers-and-duties-of-commission" tabindex="-1">§ 30-284 Powers and duties of Commission. <a class="header-anchor" href="#§-30-284-powers-and-duties-of-commission" aria-label="Permalink to &quot;§ 30-284 Powers and duties of Commission.&quot;">​</a></h1><p>The Commission shall have the following powers and duties:</p><ol><li><p>To make performance reviews of operations of state agencies with transportation responsibilities to ascertain that sums appropriated have been or are being expended for the purposes for which they were made and to evaluate the effectiveness of programs in accomplishing legislative intent;</p></li><li><p>To study, on a continuing basis, the operations, practices, and duties of state agencies with transportation responsibilities as they relate to efficiency in the use of space, personnel, equipment, and facilities;</p></li><li><p>To retain such consultants and advisers as the Commission deems necessary to evaluate financial and project management of state agencies with transportation responsibilities;</p></li><li><p>To make such special studies of and reports on the operations and functions of state agencies with transportation responsibilities as it deems appropriate and as may be requested by the General Assembly;</p></li><li><p>To review actions of the Commonwealth Transportation Board; and</p></li><li><p>To make recommendations to the General Assembly on necessary transportation legislation.</p></li></ol><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0896" target="_blank" rel="noreferrer">896</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0480" target="_blank" rel="noreferrer">480</a>.</p>',4),n=[o];function r(p,l,c,d,m,h){return a(),t("div",null,n)}const _=e(s,[["render",r]]);export{u as __pageData,_ as default};
