import{_ as e,o,c as s,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 30-34.1 Legislative Support Commission; membership; officers; compensation and expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-34.1.md","filePath":"vacode/30-34.1.md"}'),a={name:"vacode/30-34.1.md"},i=t('<h1 id="§-30-34-1-legislative-support-commission-membership-officers-compensation-and-expenses" tabindex="-1">§ 30-34.1 Legislative Support Commission; membership; officers; compensation and expenses. <a class="header-anchor" href="#§-30-34-1-legislative-support-commission-membership-officers-compensation-and-expenses" aria-label="Permalink to &quot;§ 30-34.1 Legislative Support Commission; membership; officers; compensation and expenses.&quot;">​</a></h1><p>There is hereby created the Legislative Support Commission in the legislative branch of state government, hereafter referred to as &quot;Commission.&quot; The Commission shall consist of seven members: two members of the Committee on Rules of the House of Delegates who shall be appointed by and serve at the pleasure of the chairman of such Committee; one member of the Committee on Rules of the Senate who shall be appointed by and serve at the pleasure of the chairman of such Committee; the Clerk of the House of Delegates; the Clerk of the Senate; the Director of the Division of Legislative Services; and the Director of the Division of Legislative Automated Systems. The Commission shall name from its members a chairman and such other officers as are deemed necessary.</p><p>Legislative members shall receive such compensation for the performance of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a> and all members shall be reimbursed for all reasonable and necessary expenses as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. The Office of the Clerk of the House of Delegates and the Office of the Clerk of the Senate shall provide funding for the costs of compensation and expenses of their respective members.</p><p>1980, c. 687; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>.</p>',4),r=[i];function n(m,h,l,f,c,p){return o(),s("div",null,r)}const _=e(a,[["render",n]]);export{v as __pageData,_ as default};
