import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-34.15 Submission of reports and executive summaries to the legislative branch.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-34.15.md","filePath":"vacode/30-34.15.md"}'),r={name:"vacode/30-34.15.md"},s=o('<h1 id="§-30-34-15-submission-of-reports-and-executive-summaries-to-the-legislative-branch" tabindex="-1">§ 30-34.15 Submission of reports and executive summaries to the legislative branch. <a class="header-anchor" href="#§-30-34-15-submission-of-reports-and-executive-summaries-to-the-legislative-branch" aria-label="Permalink to &quot;§ 30-34.15 Submission of reports and executive summaries to the legislative branch.&quot;">​</a></h1><p>A. Any report required or requested by law or resolution to be submitted to the General Assembly shall be submitted to the Division of Legislative Automated Systems in a written or electronic format as provided in the procedures for the processing of legislative documents. Such submission shall satisfy the requirement for communication to the General Assembly.</p><p>B. Any report required or requested by law or resolution to be submitted to any committee, subcommittee, commission, agency, or other body within the legislative branch or to the chairman or agency head of such entity shall also be submitted to the Division of Legislative Automated Systems in a written or electronic format as provided in the procedures for the processing of legislative documents and reports.</p><p>C. The reports submitted to the Division of Legislative Automated Systems shall include an executive summary. The Division shall post the executive summary and the report on the website of the General Assembly and develop a notification process to inform interested persons of such postings. Any requirement for a separate executive summary may be satisfied by the submission of a report in a written or electronic format with an executive summary.</p><p>D. The Director of the Division of Legislative Automated Systems and the publishing authority may enter into agreements to provide equivalent access to the report or the information contained in the report and such access shall satisfy the submission requirement of this section.</p><p>E. Nothing in this section shall be construed to require the release of information otherwise held confidential by law.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0650" target="_blank" rel="noreferrer">650</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0633" target="_blank" rel="noreferrer">633</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>.</p>',7),a=[s];function n(l,c,h,m,u,d){return t(),i("div",null,a)}const b=e(r,[["render",n]]);export{f as __pageData,b as default};
