import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-34.5 Printing and distribution of Acts of Assembly.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-34.5.md","filePath":"vacode/30-34.5.md"}'),n={name:"vacode/30-34.5.md"},a=i('<h1 id="§-30-34-5-printing-and-distribution-of-acts-of-assembly" tabindex="-1">§ 30-34.5 Printing and distribution of Acts of Assembly. <a class="header-anchor" href="#§-30-34-5-printing-and-distribution-of-acts-of-assembly" aria-label="Permalink to &quot;§ 30-34.5 Printing and distribution of Acts of Assembly.&quot;">​</a></h1><p>A. The Commission shall, within 45 days following the adjournment of the General Assembly sine die, send to each requesting member of the General Assembly a copy of each Act of Assembly signed by the Governor or if otherwise enacted into law, in the form in which it is signed by the Governor or otherwise enacted into law. Each act so sent shall be clearly denominated with the House of Delegates or the Senate bill number assigned to it by the respective houses of the General Assembly.</p><p>B. The Commission shall also requisition, through the Division of Legislative Automated Systems, as soon as approved by the Governor, not in excess of 5,000 copies of the acts and joint resolutions of the General Assembly. These it shall have bound in ordinary half binding, with the index and tables required by law to be printed with the acts and joint resolutions of the General Assembly, and as soon as practicable after the close of each session of the General Assembly, shall deliver by mail, express or otherwise, if requested pursuant to § <a href="/vacode/30-34.4:1/">30-34.4:1</a>:</p><ol><li><p>One copy to the Governor; and such additional copies as may be requested for use in the Governor&#39;s office;</p></li><li><p>One copy to each of the Governor&#39;s secretaries;</p></li><li><p>One copy to each head of department; each division of the Governor&#39;s office, the Commissioner of the Virginia Workers&#39; Compensation Commission, the Employment Commission and the Department of Motor Vehicles, the Director of the Department of Wildlife Resources and the Executive Secretary of the Compensation Board and the Director of the Virginia Retirement System;</p></li><li><p>As many copies to the Division of Legislative Services as may be required by the Division for its use or for exchange with other states;</p></li><li><p>One copy to each member of the General Assembly; however, up to four additional copies may be obtained upon application to the Division of Legislative Automated Systems;</p></li><li><p>One copy to the Lieutenant Governor;</p></li><li><p>One copy to each judge;</p></li><li><p>Five copies to the State Corporation Commission;</p></li><li><p>Twenty-five copies to the Attorney General;</p></li><li><p>One copy to the reporter of the Supreme Court, the Executive Secretary of the Supreme Court, and each clerk of any court, attorney for the Commonwealth, Commissioner of the Revenue, Treasurer, public library, school board, judge and clerk of any court held in this Commonwealth under the laws of the United States and each attorney and marshal in this Commonwealth holding office under the United States;</p></li><li><p>One copy to the city manager of a city, the mayor of a town and the county administrator, manager or executive depending on the county&#39;s form of government; however, an additional copy for use within the city, town or county may be obtained upon application to the Division of Legislative Automated Systems;</p></li><li><p>Five copies to The Library of Virginia;</p></li><li><p>Five copies to the State Law Library;</p></li><li><p>One copy to the head of each institution of higher education in the Commonwealth;</p></li><li><p>One copy to the library of each institution of higher education in the Commonwealth;</p></li><li><p>One copy to the Virginia School for the Deaf and the Blind;</p></li><li><p>Five copies to the Clerk of the Senate for the use of the Senate;</p></li><li><p>Ten copies to the Clerk of the House of Delegates for the use of the House;</p></li><li><p>Three copies to the Auditor of Public Accounts;</p></li><li><p>Three additional copies to the Comptroller;</p></li><li><p>One copy to the county attorney in those counties which have created the office of the county attorney;</p></li><li><p>One copy to the Joint Legislative Audit and Review Commission;</p></li><li><p>One copy to the Committee on Appropriations of the House of Delegates;</p></li><li><p>One copy to the Senate Committee on Finance and Appropriations; and</p></li><li><p>One copy to the Division of Legislative Automated Systems.</p></li></ol><p>1980, c. 687; 1983, c. 108; 1987, c. 128; 1992, c. 857; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',5),s=[a];function r(h,l,c,p,d,f){return o(),t("div",null,s)}const y=e(n,[["render",r]]);export{u as __pageData,y as default};
