import{_ as i,o,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const x=JSON.parse('{"title":"§ 30-385 (Expires July 1, 2026) Membership; terms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-385.md","filePath":"vacode/30-385.md"}'),n={name:"vacode/30-385.md"},a=e("h1",{id:"§-30-385-expires-july-1-2026-membership-terms",tabindex:"-1"},[t("§ 30-385 (Expires July 1, 2026) Membership; terms. "),e("a",{class:"header-anchor",href:"#§-30-385-expires-july-1-2026-membership-terms","aria-label":'Permalink to "§ 30-385 (Expires July 1, 2026) Membership; terms."'},"​")],-1),r=e("p",null,"The Commission shall have a total membership of 17 members that shall consist of eight legislative members, three nonlegislative citizen members, and six ex officio members. Members shall be appointed as follows: three members of the Senate, to be appointed by the Senate Committee on Rules; five members of the House of Delegates, to be appointed by the Speaker of the House of Delegates in accordance with the principles of proportional representation contained in the Rules of the House of Delegates; one nonlegislative citizen member to be appointed by the Senate Committee on Rules; one nonlegislative citizen member to be appointed by the Speaker of the House of Delegates; and one nonlegislative citizen member to be appointed by the Governor. The Superintendent of Public Instruction, the Director of the Department of General Services, the Executive Director of the Virginia Resources Authority, the State Treasurer, the President of the Board of Education, and the Director of the Department of Planning and Budget, or their respective designees, shall each serve ex officio with voting privileges. Nonlegislative citizen members of the Commission shall be citizens of the Commonwealth. Unless otherwise approved in writing by the chairman of the Commission and the respective Clerk, nonlegislative citizen members shall only be reimbursed for travel originating and ending within the Commonwealth for the purpose of attending meetings.",-1),l=e("p",null,"Legislative members and ex officio members of the Commission shall serve terms coincident with their terms of office. Appointments to fill vacancies, other than by expiration of a term, shall be for the unexpired terms. Vacancies shall be filled in the same manner as the original appointments. All members may be reappointed. Nonlegislative citizen members shall be appointed for a term of two years.",-1),m=e("p",null,"The Commission shall elect a chairman and vice-chairman from among its membership, who shall be members of the General Assembly.",-1),h=e("p",null,[t("2020, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1044",target:"_blank",rel:"noreferrer"},"1044"),t(", § 30-377.")],-1),c=[a,r,l,m,h];function p(f,d,b,g,u,v){return o(),s("div",null,c)}const y=i(n,[["render",p]]);export{x as __pageData,y as default};
