import{_ as e,o,c as i,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-388 (Expires July 1, 2026) Powers and duties of the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-388.md","filePath":"vacode/30-388.md"}'),t={name:"vacode/30-388.md"},n=s('<h1 id="§-30-388-expires-july-1-2026-powers-and-duties-of-the-commission" tabindex="-1">§ 30-388 (Expires July 1, 2026) Powers and duties of the Commission. <a class="header-anchor" href="#§-30-388-expires-july-1-2026-powers-and-duties-of-the-commission" aria-label="Permalink to &quot;§ 30-388 (Expires July 1, 2026) Powers and duties of the Commission.&quot;">​</a></h1><p>The Commission shall have the following powers and duties:</p><ol><li><p>Assessing the Commonwealth&#39;s school facilities and determining school construction and modernization funding needs.</p></li><li><p>Identifying funding mechanisms and making recommendations to the Governor and the General Assembly.</p></li><li><p>Establishing best practices in school modernization and construction for school divisions.</p></li><li><p>Creating standardized construction designs and procurement practices to recommend and make available to local school divisions.</p></li><li><p>Identifying potential cost-saving measures for implementation by local school divisions to minimize construction and modernization costs where possible.</p></li><li><p>Submitting to the General Assembly and the Governor an annual report for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The chairman of the Commission shall submit to the General Assembly and the Governor an annual executive summary of the interim activity and work of the Commission no later than the first day of each regular session of the General Assembly. The executive summary shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li></ol><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1044" target="_blank" rel="noreferrer">1044</a>, § 30-380.</p>',4),a=[n];function r(l,d,c,m,h,p){return o(),i("div",null,a)}const _=e(t,[["render",r]]);export{f as __pageData,_ as default};
