import{_ as s,o,c as n,z as e,a as r}from"./chunks/framework.9482e208.js";const v=JSON.parse(`{"title":"§ 30-4 Civil proceedings for or against members, clerks or clerks' assistants, or the Lieutenant Governor during session.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-4.md","filePath":"vacode/30-4.md"}`),t={name:"vacode/30-4.md"},i=e("h1",{id:"§-30-4-civil-proceedings-for-or-against-members-clerks-or-clerks-assistants-or-the-lieutenant-governor-during-session",tabindex:"-1"},[r("§ 30-4 Civil proceedings for or against members, clerks or clerks' assistants, or the Lieutenant Governor during session. "),e("a",{class:"header-anchor",href:"#§-30-4-civil-proceedings-for-or-against-members-clerks-or-clerks-assistants-or-the-lieutenant-governor-during-session","aria-label":`Permalink to "§ 30-4 Civil proceedings for or against members, clerks or clerks' assistants, or the Lieutenant Governor during session."`},"​")],-1),a=e("p",null,"Any action, suit or other civil proceeding, either in favor of or against a member of the General Assembly, or the clerks thereof, or the clerks' full-time assistants, the sergeant-at-arms of the Senate or House, or the Lieutenant Governor, may be commenced, but shall not, unless by their consent, be prosecuted to final judgment or decree during the session of the General Assembly. In addition, no such person shall be compelled in any civil proceeding to appear or to answer or respond, in person or in writing, nor shall any such person be taken into custody or imprisoned during the session of the General Assembly, or during the fifteen days next before the beginning or after the ending of any session.",-1),c=e("p",null,[r("Code 1919, § 297; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0471",target:"_blank",rel:"noreferrer"},"471"),r(".")],-1),l=[i,a,c];function d(h,g,m,u,f,p){return o(),n("div",null,l)}const _=s(t,[["render",d]]);export{v as __pageData,_ as default};
