import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-102.3:1.1 Continuing care retirement communities accessing medical assistance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-102.3:1.1.md","filePath":"vacode/32.1-102.3:1.1.md"}'),a={name:"vacode/32.1-102.3:1.1.md"},r=n('<h1 id="§-32-1-102-3-1-1-continuing-care-retirement-communities-accessing-medical-assistance" tabindex="-1">§ 32.1-102.3:1.1 Continuing care retirement communities accessing medical assistance. <a class="header-anchor" href="#§-32-1-102-3-1-1-continuing-care-retirement-communities-accessing-medical-assistance" aria-label="Permalink to &quot;§ 32.1-102.3:1.1 Continuing care retirement communities accessing medical assistance.&quot;">​</a></h1><p>A. A nursing facility in Planning District 8 in a continuing care retirement community registered with the State Corporation Commission pursuant to Chapter 49 (§ <a href="/vacode/38.2-4900/">38.2-4900</a> et seq.) of Title 38.2, which is not already certified for participation in the Medical Assistance Program, may be certified for participation in the Medical Assistance Program, without regard to any condition of a certificate of public need, so long as:</p><ol><li><p>The nursing facility is no longer operating under an open admissions period;</p></li><li><p>Any residents who qualify and receive medical assistance under the state program must have been residents of the continuing care retirement community for at least three years;</p></li><li><p>Not more than 25 percent of the nursing home beds of the facility, or 15 nursing home beds, whichever is fewer, may be occupied by individuals receiving benefits at any given time; and</p></li><li><p>Any resident who qualifies for and receives medical assistance under the state program in a continuing care retirement community nursing facility must have first exhausted any refundable entrance fee paid on the resident&#39;s behalf, as defined in § <a href="/vacode/38.2-4900/">38.2-4900</a>, as a result of expenditures for that resident&#39;s care in the continuing care retirement community.</p></li></ol><p>B. Nothing in this section shall alter the conditions of a continuing care retirement community&#39;s participation in the Medical Assistance Program if that continuing care retirement community was certified for participation prior to July 1, 2010.</p><p>For the purposes of this section, &quot;open admissions period&quot; means a time during which a facility may take admissions directly into its nursing home beds without the signing of a standard contract.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0857" target="_blank" rel="noreferrer">857</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0299" target="_blank" rel="noreferrer">299</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>.</p>',6),s=[r];function o(c,l,d,m,h,u){return i(),t("div",null,s)}const f=e(a,[["render",o]]);export{g as __pageData,f as default};
