import{_ as i,o as e,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-102.3:1 Application for certificate not required of certain nursing facilities or nursing homes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-102.3:1.md","filePath":"vacode/32.1-102.3:1.md"}'),r={name:"vacode/32.1-102.3:1.md"},n=a('<h1 id="§-32-1-102-3-1-application-for-certificate-not-required-of-certain-nursing-facilities-or-nursing-homes" tabindex="-1">§ 32.1-102.3:1 Application for certificate not required of certain nursing facilities or nursing homes. <a class="header-anchor" href="#§-32-1-102-3-1-application-for-certificate-not-required-of-certain-nursing-facilities-or-nursing-homes" aria-label="Permalink to &quot;§ 32.1-102.3:1 Application for certificate not required of certain nursing facilities or nursing homes.&quot;">​</a></h1><p>An application for a certificate that there exists a public need for a proposed project shall not be required for nursing facilities or nursing homes affiliated with facilities which, on January 1, 1982, and thereafter, meet all of the following criteria:</p><ol><li><p>A facility which is operated as a nonprofit institution.</p></li><li><p>A facility which is licensed jointly by the Department as a nursing facility or nursing home and by the Department of Social Services as an assisted living facility.</p></li><li><p>A facility which observes the following restrictions on admissions:</p></li></ol><p>a. Admissions are only allowed pursuant to the terms of a &quot;life care contract&quot; guaranteeing that the full complement of services offered by the facility is available to the resident as and when needed;</p><p>b. Admissions to the assisted living facility unit are restricted to individuals defined as ambulatory by the Department of Social Services;</p><p>c. Admissions to the nursing facility or nursing home unit are restricted to those individuals who are residents of the assisted living facility unit.</p><p>1982, c. 659; 1993, cc. 957, 993; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0857" target="_blank" rel="noreferrer">857</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0175" target="_blank" rel="noreferrer">175</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>.</p>',7),o=[n];function s(c,l,f,p,d,h){return e(),t("div",null,o)}const _=i(r,[["render",s]]);export{g as __pageData,_ as default};
