import{_ as n,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 32.1-11.2 Regional AIDS resource and consultation centers; HIV early intervention centers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-11.2.md","filePath":"vacode/32.1-11.2.md"}'),i={name:"vacode/32.1-11.2.md"},r=e("h1",{id:"§-32-1-11-2-regional-aids-resource-and-consultation-centers-hiv-early-intervention-centers",tabindex:"-1"},[t("§ 32.1-11.2 Regional AIDS resource and consultation centers; HIV early intervention centers. "),e("a",{class:"header-anchor",href:"#§-32-1-11-2-regional-aids-resource-and-consultation-centers-hiv-early-intervention-centers","aria-label":'Permalink to "§ 32.1-11.2 Regional AIDS resource and consultation centers; HIV early intervention centers."'},"​")],-1),s=e("p",null,"Utilizing existing state and local facilities and from such funds as are appropriated for this purpose, the Board of Health shall provide grants for no more than five regional AIDS resource and consultation centers and four HIV early intervention centers.",-1),c=e("p",null,"Each regional AIDS resource and consultation center shall be designed to address the need for expanded medical care and support services for persons with human immunodeficiency virus infection through education of health care professionals on a broad range of AIDS-related issues, clinical training for health care practitioners and students, medical consultation to community physicians and other health care providers, provision of current technical medical materials such as manuals and protocols for the management of HIV infection and medical literature, facilitation of access to health services, mental health and substance abuse services, support services and case management for HIV-infected persons. The regional AIDS resource and consultation centers shall cooperate with at least one of the medical schools located in the Commonwealth.",-1),l=e("p",null,"Each HIV early intervention center shall supply medical care and support services for persons with human immunodeficiency virus infection in accordance with its agreement with the Commissioner of Health.",-1),d=e("p",null,"The Board shall establish criteria for award of the grants. The criteria for the grants for the regional AIDS resource and consultation centers shall include, but not be limited to: (i) priority targeting of funds for services to high risk populations; (ii) geographical distribution of the centers in order to provide equal access to services throughout the Commonwealth; (iii) pro rata apportionment of funds according to the number of cases of acquired immunodeficiency syndrome in the various areas of the Commonwealth; (iv) development of innovative and flexible approaches to provision of services tailored to the specific needs of patients in the region; and (v) extensive community involvement.",-1),h=e("p",null,[t("1989, c. 613; 1993, c. 664; 1994, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0610",target:"_blank",rel:"noreferrer"},"610"),t(".")],-1),u=[r,s,c,l,d,h];function f(p,m,v,g,_,y){return o(),a("div",null,u)}const x=n(i,[["render",f]]);export{b as __pageData,x as default};
