import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-111.8 Revocation and suspension of licenses and permits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-111.8.md","filePath":"vacode/32.1-111.8.md"}'),r={name:"vacode/32.1-111.8.md"},s=n('<h1 id="§-32-1-111-8-revocation-and-suspension-of-licenses-and-permits" tabindex="-1">§ 32.1-111.8 Revocation and suspension of licenses and permits. <a class="header-anchor" href="#§-32-1-111-8-revocation-and-suspension-of-licenses-and-permits" aria-label="Permalink to &quot;§ 32.1-111.8 Revocation and suspension of licenses and permits.&quot;">​</a></h1><p>Whenever an emergency medical services agency or emergency medical services vehicle owned or operated by an emergency medical services agency is in violation of any provision of this article or any applicable regulation, the Commissioner shall have power to revoke or suspend such emergency medical services agency&#39;s license and the permits of all emergency medical services vehicles owned or operated by the emergency medical services agency. The Commissioner may temporarily suspend any license for an emergency medical services agency or permit for an emergency medical services vehicle without notice, pending a hearing or informal fact-finding conference, if the Commissioner finds that there is a substantial danger to public health or safety. When the Commissioner has temporarily suspended a license or permit pending a hearing, the Commissioner shall seek an expedited hearing in accordance with the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.).</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0899" target="_blank" rel="noreferrer">899</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0660" target="_blank" rel="noreferrer">660</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',3),o=[s];function t(c,l,d,p,h,m){return a(),i("div",null,o)}const v=e(r,[["render",t]]);export{f as __pageData,v as default};
