import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-12.1 Board to establish regulations regarding human research.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-12.1.md","filePath":"vacode/32.1-12.1.md"}'),o={name:"vacode/32.1-12.1.md"},s=t('<h1 id="§-32-1-12-1-board-to-establish-regulations-regarding-human-research" tabindex="-1">§ 32.1-12.1 Board to establish regulations regarding human research. <a class="header-anchor" href="#§-32-1-12-1-board-to-establish-regulations-regarding-human-research" aria-label="Permalink to &quot;§ 32.1-12.1 Board to establish regulations regarding human research.&quot;">​</a></h1><p>The Board shall promulgate regulations pursuant to the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) to effectuate the provisions of Chapter 5.1 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a> et seq.) of this title for human research, as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-162.16/" target="_blank" rel="noreferrer">32.1-162.16</a>, to be conducted or authorized by the Department or any facilities or other entities operated, funded, or licensed by the Department. The regulations shall require the human research committee to submit to the Governor, the General Assembly, and the Commissioner or his designee at least annually a report on the human research projects reviewed and approved by the committee and shall require the committee to report any significant deviations from the proposals as approved.</p><p>1992, c. 603.</p>',3),i=[s];function n(h,l,d,c,p,m){return a(),r("div",null,i)}const g=e(o,[["render",n]]);export{_ as __pageData,g as default};
