import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-122.10:004 Powers of the authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-122.10:004.md","filePath":"vacode/32.1-122.10:004.md"}'),r={name:"vacode/32.1-122.10:004.md"},n=a('<h1 id="§-32-1-122-10-004-powers-of-the-authority" tabindex="-1">§ 32.1-122.10:004 Powers of the authority. <a class="header-anchor" href="#§-32-1-122-10-004-powers-of-the-authority" aria-label="Permalink to &quot;§ 32.1-122.10:004 Powers of the authority.&quot;">​</a></h1><p>Any authority shall have the following powers:</p><ol><li><p>Each authority is vested with the powers of a body corporate, including the power to sue and be sued in its own name, to adopt and use a common seal and to alter the same as may be deemed expedient, to make and execute contracts and other instruments necessary or convenient to the exercise of the powers of the authority, and to make, amend or repeal bylaws, rules and regulations, not inconsistent with law, to carry into effect the powers and purposes of the authority.</p></li><li><p>To foster and stimulate the cooperative assessment and provision of health care in the community by local governments, private entities and volunteers.</p></li><li><p>To cooperate with local and state health care planning entities, and local, state or federal governments in the discharge of its duties.</p></li><li><p>To solicit and accept grants or donations from local, state or federal governments or any instrumentality thereof, private entities, or any other source, public or private, for or in aid of any project of the authority to provide health services as defined in subsection A of § <a href="http://law.lis.virginia.gov/vacode/32.1-122.10:001/" target="_blank" rel="noreferrer">32.1-122.10:001</a>.</p></li><li><p>To do any and all other acts and things that may be reasonably necessary and convenient to carry out its purposes and powers.</p></li></ol><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0671" target="_blank" rel="noreferrer">671</a>.</p>',4),i=[n];function s(l,c,h,d,p,f){return t(),o("div",null,i)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
