import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 32.1-122.20 Recruitment and retention of health care providers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-122.20.md","filePath":"vacode/32.1-122.20.md"}'),i={name:"vacode/32.1-122.20.md"},n=t('<h1 id="§-32-1-122-20-recruitment-and-retention-of-health-care-providers" tabindex="-1">§ 32.1-122.20 Recruitment and retention of health care providers. <a class="header-anchor" href="#§-32-1-122-20-recruitment-and-retention-of-health-care-providers" aria-label="Permalink to &quot;§ 32.1-122.20 Recruitment and retention of health care providers.&quot;">​</a></h1><p>The Commissioner shall direct the Commonwealth&#39;s activities and programs for recruiting and retaining health care providers for underserved populations, underserved areas, and health professional shortage areas (HPSAs) designated throughout the Commonwealth. The duties and responsibilities of the Commissioner shall include, but not be limited to:</p><ol><li><p>Designating and updating as necessary the designation of underserved areas that meet the criteria established by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-122.5/" target="_blank" rel="noreferrer">32.1-122.5</a>;</p></li><li><p>Designating and updating as necessary those areas of the state which meet the criteria of dental, primary care and mental health professional shortage areas as provided in 42 C.F.R. Part 5;</p></li><li><p>Administering the scholarship and loan repayment programs pursuant to Article 6 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-122.5/" target="_blank" rel="noreferrer">32.1-122.5</a> et seq.) of this chapter as well as any other programs or activities authorized in the appropriation act for recruiting and retaining providers for the Commonwealth&#39;s underserved populations, underserved areas and HPSAs;</p></li><li><p>Recruiting health care providers, residents, and students in Virginia and other states to care for Virginia&#39;s underserved populations and practice in underserved areas and HPSAs throughout the Commonwealth;</p></li><li><p>Publicizing the functions, programs, and activities of the Department available to assist providers in establishing a practice in underserved areas and HPSAs throughout the Commonwealth;</p></li><li><p>Coordinating the Department&#39;s health workforce activities with other state agencies as well as public and private entities in Virginia involved in health workforce training, recruitment, and retention; and</p></li><li><p>Identifying and recommending to the Governor and the General Assembly new programs, activities, and strategies for increasing the number of providers practicing in Virginia&#39;s underserved areas and HPSAs and serving Virginia&#39;s underserved populations.</p></li></ol><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0175" target="_blank" rel="noreferrer">175</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>.</p>',4),o=[n];function s(d,l,h,p,c,g){return r(),a("div",null,o)}const m=e(i,[["render",s]]);export{v as __pageData,m as default};
