import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-122.6:1 Physician Loan Repayment Program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-122.6:1.md","filePath":"vacode/32.1-122.6:1.md"}'),t={name:"vacode/32.1-122.6:1.md"},o=i('<h1 id="§-32-1-122-6-1-physician-loan-repayment-program" tabindex="-1">§ 32.1-122.6:1 Physician Loan Repayment Program. <a class="header-anchor" href="#§-32-1-122-6-1-physician-loan-repayment-program" aria-label="Permalink to &quot;§ 32.1-122.6:1 Physician Loan Repayment Program.&quot;">​</a></h1><p>A. With such funds as are appropriated for this purpose, the Board of Health shall establish a physician loan repayment program for graduates of accredited medical schools who have a specialty in the primary care areas of family practice medicine, general internal medicine, pediatrics, and obstetrics/gynecology, or who are currently employed in a geriatrics fellowship, and who meet other criteria as determined by the Board. The Commissioner shall act as the fiscal agent for the Board in administration of these funds. Prior to awarding any funds, the Board shall require the recipient to agree to perform a period of medical service in the Commonwealth in a medically underserved area as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-122.5/" target="_blank" rel="noreferrer">32.1-122.5</a> or a health professional shortage area designated in accordance with the criteria established in 42 C.F.R. Part 5, or, in the case of graduates of accredited medical schools who are currently employed in a geriatrics fellowship, the Board shall require the recipient to agree to perform, at minimum, a two-year period of medical service in the Commonwealth.</p><p>B. The Board shall promulgate regulations for the implementation of the Physician Loan Repayment Program. Applications for participation in the program will be accepted from a graduate of any accredited medical school, but preference will be given to graduates of medical schools located in the Commonwealth.</p><p>C. Any loan repayment amounts repaid by recipients who fail to honor the obligation to perform a period of medical service in an underserved area or a two-year period as required by this section, and any interest thereon, shall be used only for the purposes of this section and shall not revert to the general fund.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0111" target="_blank" rel="noreferrer">111</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0926" target="_blank" rel="noreferrer">926</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0255" target="_blank" rel="noreferrer">255</a>.</p>',5),n=[o];function l(s,c,h,d,p,m){return a(),r("div",null,n)}const _=e(t,[["render",l]]);export{g as __pageData,_ as default};
