import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-122.9:1 Dentist Loan Repayment Program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-122.9:1.md","filePath":"vacode/32.1-122.9:1.md"}'),i={name:"vacode/32.1-122.9:1.md"},n=t('<h1 id="§-32-1-122-9-1-dentist-loan-repayment-program" tabindex="-1">§ 32.1-122.9:1 Dentist Loan Repayment Program. <a class="header-anchor" href="#§-32-1-122-9-1-dentist-loan-repayment-program" aria-label="Permalink to &quot;§ 32.1-122.9:1 Dentist Loan Repayment Program.&quot;">​</a></h1><p>A. With such funds as are appropriated for this purpose, the Board shall establish a dentist loan repayment program for graduates of accredited dental schools who meet the criteria determined by the Board. The Commissioner shall act as the fiscal agent for the Board in administration of these funds. Prior to awarding any funds, the Board shall require the recipient to agree to perform a period of dental service in this Commonwealth in an underserved area as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-122.5/" target="_blank" rel="noreferrer">32.1-122.5</a> or a dental health professional shortage area designated in accordance with the criteria established in 42 C.F.R. Part 5. The Board also shall ensure that recipients (i) participate in the Commonwealth&#39;s medical assistance services program established pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-325/" target="_blank" rel="noreferrer">32.1-325</a> and the Family Access to Medical Insurance Security Plan established pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-351/" target="_blank" rel="noreferrer">32.1-351</a>, and (ii) do not limit the number of persons enrolled in these programs who are admitted to their dental practice.</p><p>B. Applications for participation in the program will be accepted from a graduate of any accredited dental school, but preference will be given to graduates of Virginia Commonwealth University&#39;s School of Dentistry.</p><p>C. Any loan repayment amounts repaid by recipients who fail to honor the obligation to perform a period of dental service in an underserved area as required by this section, and any interest thereon, shall be used only for the purposes of this section and shall not revert to the general fund.</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0174" target="_blank" rel="noreferrer">174</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0202" target="_blank" rel="noreferrer">202</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>.</p>',5),o=[n];function s(l,d,h,c,p,f){return a(),r("div",null,o)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
