import{_ as a,o as e,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-125.3 Bed capacity and licensure in hospitals designated as critical access hospitals; designation as rural hospital.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-125.3.md","filePath":"vacode/32.1-125.3.md"}'),s={name:"vacode/32.1-125.3.md"},r=t('<h1 id="§-32-1-125-3-bed-capacity-and-licensure-in-hospitals-designated-as-critical-access-hospitals-designation-as-rural-hospital" tabindex="-1">§ 32.1-125.3 Bed capacity and licensure in hospitals designated as critical access hospitals; designation as rural hospital. <a class="header-anchor" href="#§-32-1-125-3-bed-capacity-and-licensure-in-hospitals-designated-as-critical-access-hospitals-designation-as-rural-hospital" aria-label="Permalink to &quot;§ 32.1-125.3 Bed capacity and licensure in hospitals designated as critical access hospitals; designation as rural hospital.&quot;">​</a></h1><p>A. Any medical care facility licensed as a hospital pursuant to this article that (i) has been certified, as provided in § <a href="http://law.lis.virginia.gov/vacode/32.1-122.07/" target="_blank" rel="noreferrer">32.1-122.07</a>, as a critical access hospital by the Commissioner of Health in compliance with the certification regulations promulgated by the Health Care Financing Administration pursuant to Title XVIII of the Social Security Act, as amended, and (ii) has, as a result of the critical access certification, been required to reduce its licensed bed capacity to conform to the critical access certification requirement shall, upon termination of its critical access hospital certification, be licensed to operate at the licensed bed capacity in existence prior to the critical access hospital certification without being required to apply for and obtain a certificate of public need for such bed capacity in accordance with Article 1.1 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-102.1/" target="_blank" rel="noreferrer">32.1-102.1</a> et seq.) of Chapter 4 of this title.</p><p>B. Any medical care facility licensed as a hospital shall be considered a rural hospital on and after September 30, 2004, pursuant to 42 U.S.C. § 1395ww (d)(8)(E)(ii)(II), if (i) the hospital is located in an area defined as rural by federal statute or regulation; (ii) the Board of Health defines, in regulation, the area in which the hospital is located as a rural health area or the hospital as a rural hospital; or (iii) the hospital was designated, prior to October 1, 2004, as a Medicare-dependent small rural health hospital, as defined in 42 U.S.C. § 1395ww (d)(5)(G)(iv).</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0579" target="_blank" rel="noreferrer">579</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0378" target="_blank" rel="noreferrer">378</a>.</p>',4),c=[r];function o(n,l,d,h,p,f){return e(),i("div",null,c)}const _=a(s,[["render",o]]);export{g as __pageData,_ as default};
