import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-135.2 Offer or payment of remuneration in exchange for referral prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-135.2.md","filePath":"vacode/32.1-135.2.md"}'),o={name:"vacode/32.1-135.2.md"},n=t('<h1 id="§-32-1-135-2-offer-or-payment-of-remuneration-in-exchange-for-referral-prohibited" tabindex="-1">§ 32.1-135.2 Offer or payment of remuneration in exchange for referral prohibited. <a class="header-anchor" href="#§-32-1-135-2-offer-or-payment-of-remuneration-in-exchange-for-referral-prohibited" aria-label="Permalink to &quot;§ 32.1-135.2 Offer or payment of remuneration in exchange for referral prohibited.&quot;">​</a></h1><p>No hospital licensed pursuant to this chapter shall knowingly and willfully offer or pay any remuneration directly or indirectly, in cash or in kind, to induce any practitioner of the healing arts or any clinical psychologist to refer an individual or individuals to such hospital. The Board shall adopt regulations as necessary to carry out the provisions of this section. Such regulations shall be developed in conjunction with the State Board of Behavioral Health and Developmental Services and shall be consistent with regulations adopted by such Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/37.2-420/" target="_blank" rel="noreferrer">37.2-420</a>. Such regulations shall exclude from the definition of &quot;remuneration&quot; any payments, business arrangements, or payment practices not prohibited by 42 U.S.C. § 1320a, as amended, or any regulations promulgated pursuant thereto.</p><p>1990, c. 379; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0980" target="_blank" rel="noreferrer">980</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>.</p>',3),i=[n];function l(s,c,h,f,p,d){return r(),a("div",null,i)}const _=e(o,[["render",l]]);export{g as __pageData,_ as default};
