import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-137.8 Application to and compliance by utilization review entities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-137.8.md","filePath":"vacode/32.1-137.8.md"}'),r={name:"vacode/32.1-137.8.md"},o=a('<h1 id="§-32-1-137-8-application-to-and-compliance-by-utilization-review-entities" tabindex="-1">§ 32.1-137.8 Application to and compliance by utilization review entities. <a class="header-anchor" href="#§-32-1-137-8-application-to-and-compliance-by-utilization-review-entities" aria-label="Permalink to &quot;§ 32.1-137.8 Application to and compliance by utilization review entities.&quot;">​</a></h1><p>A. No utilization review entity shall perform utilization review with regard to hospital, medical or other health care resources rendered or proposed to be rendered to a covered person except in accordance with the requirements and standards set forth in this article.</p><p>B. This article shall not apply to utilization review performed under contract with the federal government for utilization review of patients eligible for hospital services under Title XVIII of the Social Security Act or under contract with a plan otherwise exempt from operation of this chapter pursuant to the Employee Retirement Income Security Act of 1974.</p><p>C. This article shall not apply to private review agents subject to Article 2.1 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-138.6/" target="_blank" rel="noreferrer">32.1-138.6</a> et seq.) of Chapter 5 of Title 32.1 of the Code of Virginia.</p><p>D. This article shall not apply to programs administered by the Department of Medical Assistance Services or under contract with the Department of Medical Assistance Services.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0129" target="_blank" rel="noreferrer">129</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>.</p>',6),n=[o];function c(l,s,p,d,h,f){return t(),i("div",null,n)}const u=e(r,[["render",c]]);export{m as __pageData,u as default};
