import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 32.1-138.9 Standards for approval.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-138.9.md","filePath":"vacode/32.1-138.9.md"}'),i={name:"vacode/32.1-138.9.md"},o=r('<h1 id="§-32-1-138-9-standards-for-approval" tabindex="-1">§ 32.1-138.9 Standards for approval. <a class="header-anchor" href="#§-32-1-138-9-standards-for-approval" aria-label="Permalink to &quot;§ 32.1-138.9 Standards for approval.&quot;">​</a></h1><p>Each private review agent shall file an application with the Department which shall meet the following minimum standards and any additional standards established by regulation pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-138.15/" target="_blank" rel="noreferrer">32.1-138.15</a>, and pay a filing fee established by the Department, in order to be approved by the Department:</p><ol><li><p>A description of the procedures to be used in evaluating proposed or delivered hospital, medical, or other health care services;</p></li><li><p>The procedures by which patients or providers may seek reconsideration of determinations by private review agents;</p></li><li><p>The type and qualifications of the personnel either employed or under contract to perform the utilization review;</p></li><li><p>Procedures and policies which ensure that patient-specific medical records and information shall be kept strictly confidential except as authorized by the patient or by regulations adopted pursuant to this article; and</p></li><li><p>Assurances that reviewers be readily accessible by telephone to patients and providers at least forty hours per week during normal business hours.</p></li></ol><p>1990, c. 826, § 38.2-5302; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0129" target="_blank" rel="noreferrer">129</a>.</p>',4),n=[o];function s(l,p,d,c,h,f){return a(),t("div",null,n)}const v=e(i,[["render",s]])