import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 32.1-162.15:2 (Effective July 1, 2023) Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-162.15:2.md","filePath":"vacode/32.1-162.15:2.md"}'),r={name:"vacode/32.1-162.15:2.md"},s=i('<h1 id="§-32-1-162-15-2-effective-july-1-2023-definitions" tabindex="-1">§ 32.1-162.15:2 (Effective July 1, 2023) Definitions. <a class="header-anchor" href="#§-32-1-162-15-2-effective-july-1-2023-definitions" aria-label="Permalink to &quot;§ 32.1-162.15:2 (Effective July 1, 2023) Definitions.&quot;">​</a></h1><p>&quot;Anonymous physical evidence recovery kit&quot; has the same meaning as in § <a href="/vacode/19.2-11.5/">19.2-11.5</a>.</p><p>&quot;Approved pediatric health care facility&quot; means a pediatric health care facility for which a plan for the delivery of services to pediatric survivors of sexual assault has been approved pursuant to § <a href="/vacode/32.1-162.15:6/">32.1-162.15:6</a>.</p><p>&quot;Board&quot; means the Board of Health.</p><p>&quot;Department&quot; means the Department of Health.</p><p>&quot;Emergency contraception&quot; means medication approved by the U.S. Food and Drug Administration that can significantly reduce the risk of pregnancy if taken within 72 hours after sexual assault.</p><p>&quot;Follow-up health care&quot; means any physical examination, laboratory tests to determine the presence of sexually transmitted infection, or appropriate medications, including HIV-prophylaxis, provided to a survivor of sexual assault by a health care provider within 90 days after the date on which treatment or transfer services pursuant to this article are first provided.</p><p>&quot;Forensic medical examination&quot; means health care services provided to a survivor of sexual assault that include medical history, physical examination, laboratory testing, assessment for drug-facilitated or alcohol-facilitated sexual assault, collection of evidence in accordance with the requirements of Chapter 1.2 (§ <a href="/vacode/19.2-11.5/">19.2-11.5</a> et seq.) of Title 19.2, and discharge and follow-up health care planning necessary to ensure the health, safety, and welfare of the survivor of sexual assault and the collection and preservation of evidence that may be used in a criminal proceeding.</p><p>&quot;Hospital&quot; means any hospital licensed by the Department pursuant to this chapter.</p><p>&quot;Pediatric health care facility&quot; means a hospital, clinic, or physician&#39;s office that provides health care services to pediatric patients.</p><p>&quot;Pediatric survivor of sexual assault&quot; means a survivor of sexual assault who is under 18 years of age.</p><p>&quot;Physical evidence recovery kit&quot; has the same meaning as in § <a href="/vacode/19.2-11.5/">19.2-11.5</a>.</p><p>&quot;Sexual assault forensic examiner&quot; means a sexual assault nurse examiner, physician, physician assistant, nurse practitioner, or registered nurse who has completed training that meets or is substantially similar to the Sexual Assault Nurse Examiner Education Guidelines established by the International Association of Forensic Nurses.</p><p>&quot;Sexual assault survivor transfer services&quot; means an appropriate medical examination and such stabilizing treatment as may be necessary prior to the transfer of a sexual assault survivor from a transfer hospital to a treatment hospital in accordance with the provisions of a transfer plan approved by the Department.</p><p>&quot;Sexual assault survivor treatment services&quot; means a forensic medical examination and other health care services provided to a sexual assault survivor by a hospital in accordance with § <a href="/vacode/32.1-162.15:4/">32.1-162.15:4</a> or pediatric health care facility in accordance with § <a href="/vacode/32.1-162.15:6/">32.1-162.15:6</a>.</p><p>&quot;Transfer hospital&quot; means a hospital with a sexual assault survivor transfer plan approved by the Department.</p><p>&quot;Transportation service&quot; means transportation provided to a survivor of sexual assault who is transferred from a transfer hospital, treatment hospital, or approved pediatric health care facility to a treatment hospital or approved pediatric care facility pursuant to a transfer plan approved in accordance with this article.</p><p>&quot;Treatment hospital&quot; means a hospital with a sexual assault survivor treatment plan approved by the Department to provide sexual assault survivor treatment services to all survivors of sexual assault who present with a complaint of sexual assault within the previous seven days or who have disclosed past sexual assault by a specific individual and were in the care of that individual within the previous seven days.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0725" target="_blank" rel="noreferrer">725</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0520" target="_blank" rel="noreferrer">520</a>.</p>',19),o=[s];function n(l,c,p,u,h,d){return a(),t("div",null,o)}const m=e(r,[["render",n]]);export{v as __pageData,m as default};
