import{_ as a,o as e,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 32.1-162.15:5 Transfer services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-162.15:5.md","filePath":"vacode/32.1-162.15:5.md"}'),s={name:"vacode/32.1-162.15:5.md"},o=t('<h1 id="§-32-1-162-15-5-transfer-services" tabindex="-1">§ 32.1-162.15:5 Transfer services. <a class="header-anchor" href="#§-32-1-162-15-5-transfer-services" aria-label="Permalink to &quot;§ 32.1-162.15:5 Transfer services.&quot;">​</a></h1><p>The Board shall adopt regulations to establish standards for review and approval of sexual assault survivor transfer plans and pediatric sexual assault survivor transfer plans, which shall include provisions for the following services, when ordered by a health care provider and with the consent of the survivor of sexual assault:</p><ol><li><p>Appropriate medical examination and such stabilizing treatment as may be necessary prior to the transfer of a survivor of sexual assault from the transfer hospital to a treatment hospital or clinic that provides treatment services for survivors of sexual assault that are comparable to those described in § <a href="/vacode/32.1-162.15:4/">32.1-162.15:4</a>;</p></li><li><p>Medically and factually accurate written and oral information about emergency contraception, the indications and contraindications and potential risks associated with the use of emergency contraception, and the availability of emergency contraception for survivors of sexual assault; and</p></li><li><p>Prompt transfer of the survivor of sexual assault to a treatment hospital, approved pediatric health care facility, or clinic that provides treatment services for survivors of sexual assault that are comparable to those described in § <a href="/vacode/32.1-162.15:4/">32.1-162.15:4</a>, as may be appropriate, including provisions necessary to ensure that transfer of the survivor of sexual assault or pediatric survivor of sexual assault would not unduly burden the survivor of sexual assault or pediatric survivor of sexual assault.</p></li></ol><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0725" target="_blank" rel="noreferrer">725</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0520" target="_blank" rel="noreferrer">520</a>.</p>',4),i=[o];function n(l,c,d,p,u,f){return e(),r("div",null,i)}const _=a(s,[["render",n]]);export{v as __pageData,_ as default};
