import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-162.3 License required for hospice programs; notice of denial of license; renewal thereof.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-162.3.md","filePath":"vacode/32.1-162.3.md"}'),o={name:"vacode/32.1-162.3.md"},s=a('<h1 id="§-32-1-162-3-license-required-for-hospice-programs-notice-of-denial-of-license-renewal-thereof" tabindex="-1">§ 32.1-162.3 License required for hospice programs; notice of denial of license; renewal thereof. <a class="header-anchor" href="#§-32-1-162-3-license-required-for-hospice-programs-notice-of-denial-of-license-renewal-thereof" aria-label="Permalink to &quot;§ 32.1-162.3 License required for hospice programs; notice of denial of license; renewal thereof.&quot;">​</a></h1><p>A. No person shall establish or operate a hospice or a hospice facility without a license issued pursuant to this article unless he is exempt from licensure pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-162.2/" target="_blank" rel="noreferrer">32.1-162.2</a>.</p><p>B. The Commissioner shall issue or renew a license to establish or operate a hospice or a hospice facility upon application therefor on a form and accompanied by a fee prescribed by the Board if the Commissioner finds that the hospice or hospice facility is in compliance with the provisions of this article and regulations of the Board. The Commissioner shall notify by certified mail any applicant denied a license of the reasons for such denial.</p><p>C. Every such license shall expire at midnight December 31 of the year issued, or as otherwise specified by the Board, and shall be required to be renewed annually.</p><p>D. The activities and services of each applicant for issuance or renewal of a hospice license shall be subject to an inspection and examination by the Commissioner to determine if the hospice is in compliance with the provisions of this article and regulations of the Board.</p><p>E. No license issued pursuant to this article may be transferred or assigned.</p><p>1981, c. 346; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0526" target="_blank" rel="noreferrer">526</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>.</p>',7),t=[s];function n(c,l,h,p,f,d){return i(),r("div",null,t)}const _=e(o,[["render",n]]);export{m as __pageData,_ as default};
