import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-166.6 Review Board to hear appeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-166.6.md","filePath":"vacode/32.1-166.6.md"}'),i={name:"vacode/32.1-166.6.md"},o=r('<h1 id="§-32-1-166-6-review-board-to-hear-appeals" tabindex="-1">§ 32.1-166.6 Review Board to hear appeals. <a class="header-anchor" href="#§-32-1-166-6-review-board-to-hear-appeals" aria-label="Permalink to &quot;§ 32.1-166.6 Review Board to hear appeals.&quot;">​</a></h1><p>The Review Board shall hear all administrative appeals of denials of onsite sewage disposal system permits and appeals of refusals of indemnification requests filed pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-164.1:01/" target="_blank" rel="noreferrer">32.1-164.1:01</a>, and refusals of betterment loan eligibility letters pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-164.1:2/" target="_blank" rel="noreferrer">32.1-164.1:2</a>, and render its decision on any such appeal, which decision shall be the final administrative decision. Proceedings of the Review Board and appeals of its decisions shall be governed by the provisions of Chapter 40 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.) of Title 2.2.</p><p>In addition to the authority to render a final administrative decision, the Review Board, in its discretion, may develop recommendations for alternative solutions to the conditions resulting in denial of the permit or refusal to indemnify and remand the case to the Department of Health for reconsideration.</p><p>For purposes of this section &quot;betterment loan&quot; is as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-163/" target="_blank" rel="noreferrer">32.1-163</a>.</p><p>1984, c. 457; 1986, c. 331; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>.</p>',5),n=[o];function s(l,d,p,c,h,f){return a(),t("div",null,n)}const g=e(i,[["render",s]]);export{_ as __pageData,g as default};
