import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 32.1-175.01 Issuance of special orders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-175.01.md","filePath":"vacode/32.1-175.01.md"}'),i={name:"vacode/32.1-175.01.md"},o=t('<h1 id="§-32-1-175-01-issuance-of-special-orders" tabindex="-1">§ 32.1-175.01 Issuance of special orders. <a class="header-anchor" href="#§-32-1-175-01-issuance-of-special-orders" aria-label="Permalink to &quot;§ 32.1-175.01 Issuance of special orders.&quot;">​</a></h1><p>Notwithstanding any other provision of law and to the extent consistent with federal requirements, following a proceeding as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-4019/" target="_blank" rel="noreferrer">2.2-4019</a>, the Board may issue a special order that may include a civil penalty against an owner who violates this article or any order or regulation adopted thereto by the Board. The issuance of a special order shall be considered a case decision as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-4001/" target="_blank" rel="noreferrer">2.2-4001</a>. Civil penalties collected pursuant to this section shall be paid into the state treasury and credited to the Water Supply Assistance Grant Fund created pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-171.2/" target="_blank" rel="noreferrer">32.1-171.2</a>.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0342" target="_blank" rel="noreferrer">342</a>, § 32.1-176.1:1; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0786" target="_blank" rel="noreferrer">786</a>.</p>',3),s=[o];function n(l,c,d,p,h,_){return a(),r("div",null,s)}const g=e(i,[["render",n]]);export{u as __pageData,g as default};
