import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-176 Penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-176.md","filePath":"vacode/32.1-176.md"}'),r={name:"vacode/32.1-176.md"},n=o('<h1 id="§-32-1-176-penalty" tabindex="-1">§ 32.1-176 Penalty. <a class="header-anchor" href="#§-32-1-176-penalty" aria-label="Permalink to &quot;§ 32.1-176 Penalty.&quot;">​</a></h1><p>In addition to the provisions of § <a href="http://law.lis.virginia.gov/vacode/32.1-27/" target="_blank" rel="noreferrer">32.1-27</a>, any owner who violates any provisions of this article or any order or regulation adopted pursuant thereto shall, upon such finding by a court of competent jurisdiction, be assessed a civil penalty of not more than $5,000 for each day of such violation. All penalties under this section shall be recovered in a civil action brought by the Attorney General in the name of the Commonwealth. Civil penalties collected pursuant to this section shall be paid into the state treasury and credited to the Water Supply Assistance Grant Fund created pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-171.2/" target="_blank" rel="noreferrer">32.1-171.2</a>.</p><p>Code 1950, § 62.1-59; 1964, c. 475; 1968, c. 659; 1977, c. 7; 1979, c. 711; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0786" target="_blank" rel="noreferrer">786</a>.</p>',3),i=[n];function s(l,c,d,h,p,_){return t(),a("div",null,i)}const v=e(r,[["render",s]]);export{f as __pageData,v as default};
