import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-19 Duties prescribed by Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-19.md","filePath":"vacode/32.1-19.md"}'),i={name:"vacode/32.1-19.md"},o=a('<h1 id="§-32-1-19-duties-prescribed-by-board" tabindex="-1">§ 32.1-19 Duties prescribed by Board. <a class="header-anchor" href="#§-32-1-19-duties-prescribed-by-board" aria-label="Permalink to &quot;§ 32.1-19 Duties prescribed by Board.&quot;">​</a></h1><p>A. The Commissioner shall perform such duties as the Board may require, in addition to the duties required by law.</p><p>B. The Commissioner shall, along with the Superintendent of Public Instruction, work to combat childhood obesity and other chronic health conditions that affect school-age children.</p><p>C. The Commissioner shall ensure, in the licensure of health care facilities, that quality of care, patient safety, and patient privacy are the overriding goals of such licensure and related enforcement efforts.</p><p>D. The Commissioner shall coordinate the Department&#39;s emergency preparedness and response efforts.</p><p>E. The Commissioner shall ensure that prevention of disease and protection of public health remain the Department&#39;s overriding goals.</p><p>F. The Commissioner shall designate a senior staff member of the Department, who shall be a licensed physician, to oversee minority health efforts of the Department.</p><p>G. The Commissioner shall designate a senior official of the Department, who shall be a licensed physician or nurse practitioner, to coordinate all women&#39;s health efforts in the Department including, but not limited to, the &quot;Every Woman&#39;s Life Program,&quot; and other efforts to prevent, detect, and treat breast cancer, cervical cancer, and other diseases that primarily affect women.</p><p>Code 1950, § 32-27; 1979, c. 711; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0320" target="_blank" rel="noreferrer">320</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>.</p>',9),n=[o];function s(l,h,c,d,p,f){return r(),t("div",null,n)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
