import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-212 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-212.md","filePath":"vacode/32.1-212.md"}'),a={name:"vacode/32.1-212.md"},n=r('<h1 id="§-32-1-212-definitions" tabindex="-1">§ 32.1-212 Definitions. <a class="header-anchor" href="#§-32-1-212-definitions" aria-label="Permalink to &quot;§ 32.1-212 Definitions.&quot;">​</a></h1><p>As used in this article unless the context otherwise requires:</p><p>&quot;Bedding&quot; means any mattress, mattress pad, box spring, upholstered bed, davenport, upholstered sofa bed, quilted pad, comforter, bolster, cushion, pillow, featherbed, sleeping bag, or any other bag, case or cover made of leather, textile or other material which is stuffed or filled in whole or in part with concealed substance, which can be used by any human being for sleeping or reclining purposes.</p><p>&quot;Filling material&quot; means cotton, wool, feathers, kapok, down, plant or vegetable fibers, or any other material or substance or combination thereof, loose or in batting, pads, or any prefabricated form, that is used or that may be used in articles of bedding or upholstered furniture.</p><p>&quot;Importer&quot; means any person who for the purpose of manufacture or resale receives bedding, upholstered furniture or filling material from any country other than the United States.</p><p>&quot;New&quot; means not previously used for any purpose. Manufacturing processes shall not be considered a prior use.</p><p>&quot;Sanitize&quot; means to reduce the level of microbiological agents to a level not injurious to health.</p><p>&quot;Secondhand&quot; means having been made prior use of or containing any filling material of which prior use has been made.</p><p>&quot;Shoddy&quot; means any material which has been spun into yarn, knit or woven into fabric and subsequently cut up, torn up, broken or ground up.</p><p>&quot;Sterilize&quot; means to render free of viable microbiological agents.</p><p>&quot;Upholstered furniture&quot; means any article of furniture designed to be used for sitting, resting, or reclining that is wholly or partly stuffed or filled with any filling material.</p><p>Code 1950, § 32-117; 1952, c. 530; 1956, c. 530; 1979, c. 711; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0391" target="_blank" rel="noreferrer">391</a>.</p>',12),i=[n];function s(l,u,d,p,c,h){return o(),t("div",null,i)}const b=e(a,[["render",s]]);export{m as __pageData,b as default};
