import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-215 Disposal restricted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-215.md","filePath":"vacode/32.1-215.md"}'),o={name:"vacode/32.1-215.md"},s=t('<h1 id="§-32-1-215-disposal-restricted" tabindex="-1">§ 32.1-215 Disposal restricted. <a class="header-anchor" href="#§-32-1-215-disposal-restricted" aria-label="Permalink to &quot;§ 32.1-215 Disposal restricted.&quot;">​</a></h1><p>No person engaged in commerce shall rent, offer or expose for sale, barter, give away, or dispose of in any other commercial manner any article of bedding or upholstered furniture made, remade, reupholstered, or renovated in violation of § <a href="http://law.lis.virginia.gov/vacode/32.1-213/" target="_blank" rel="noreferrer">32.1-213</a> or <a href="http://law.lis.virginia.gov/vacode/32.1-214/" target="_blank" rel="noreferrer">32.1-214</a> or any secondhand article of bedding or upholstered furniture unless since last used such secondhand article has been sanitized by a reasonable process approved by the Commissioner. However, a retailer may sell, give away, or rent used upholstered furniture when the used upholstered furniture has been purchased by the retailer as new furniture and has been used in the course of business. Such used furniture shall be (i) conspicuously identified as used furniture and (ii) reduced in price, sold at auction, donated to charity, or made available for a rental fee, and so tagged.</p><p>Code 1950, § 32-119; 1952, c. 530; 1956, c. 530; 1979, c. 711; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0391" target="_blank" rel="noreferrer">391</a>.</p>',3),i=[s];function n(d,l,c,u,h,p){return r(),a("div",null,i)}const m=e(o,[["render",n]]);export{_ as __pageData,m as default};
