import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-225 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-225.md","filePath":"vacode/32.1-225.md"}'),i={name:"vacode/32.1-225.md"},s=r('<h1 id="§-32-1-225-exemptions" tabindex="-1">§ 32.1-225 Exemptions. <a class="header-anchor" href="#§-32-1-225-exemptions" aria-label="Permalink to &quot;§ 32.1-225 Exemptions.&quot;">​</a></h1><p>A. The provisions of this article shall not apply to:</p><ol><li><p>Any items of bedding or upholstered furniture sold under the order of any court or pursuant to § <a href="/vacode/55.1-2902/">55.1-2902</a>, any sale of a decedent&#39;s estate or any sale by any individual of his household effects.</p></li><li><p>Any interstate public carrier.</p></li><li><p>Any state institution, agency or department unless such institution, agency or department offers for sale to the public items of bedding or upholstered furniture manufactured, reupholstered or renovated by it.</p></li><li><p>Any retailer who sells, gives away, or rents used upholstered furniture that has been purchased by the retailer as new furniture and has been used in the course of business when such used furniture has been (i) conspicuously identified as used furniture and (ii) reduced in price, sold at auction, donated to charity, or made available for a rental fee, and so tagged.</p></li></ol><p>B. Any person who sells at retail, exclusively on a consignment basis, articles of bedding that are handmade by individuals and whose gross annual receipts from the sale of such articles are not in excess of $2,000 shall be deemed to be the manufacturer of such articles and shall not be required to obtain a license to make such articles. Each such article shall have a label affixed stating the kind of filling materials used in such article but shall be exempt from any other requirement as to tags set forth in this article.</p><p>Code 1950, § 32-127; 1979, c. 711; 1989, c. 130; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0454" target="_blank" rel="noreferrer">454</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0391" target="_blank" rel="noreferrer">391</a>.</p>',5),n=[s];function o(l,d,c,u,h,p){return t(),a("div",null,n)}const _=e(i,[["render",o]]);export{m as __pageData,_ as default};
