import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-228.1 Department designated state radiation control agency; powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-228.1.md","filePath":"vacode/32.1-228.1.md"}'),n={name:"vacode/32.1-228.1.md"},o=t('<h1 id="§-32-1-228-1-department-designated-state-radiation-control-agency-powers-and-duties" tabindex="-1">§ 32.1-228.1 Department designated state radiation control agency; powers and duties. <a class="header-anchor" href="#§-32-1-228-1-department-designated-state-radiation-control-agency-powers-and-duties" aria-label="Permalink to &quot;§ 32.1-228.1 Department designated state radiation control agency; powers and duties.&quot;">​</a></h1><p>A. The Department of Health is hereby designated as the state radiation control agency. The Commissioner of Health may employ, compensate, and prescribe the duties of such individuals as may be necessary to discharge the responsibilities imposed by this article.</p><p>B. The Department shall:</p><ol><li>Collect and disseminate information relating to control of sources of radiation including:</li></ol><p>a. Establishing and maintaining a file of all applications for, issuances, denials, transfers, renewals, modifications, suspensions and revocations of, and amendments to all licenses;</p><p>b. Establishing and maintaining a file of registrants possessing sources of radiation requiring registration under the provisions of this article and any administrative or judicial action pertaining thereto; and</p><p>c. Establishing and maintaining a file of all agency rules and regulations related to regulation of sources of radiation, pending or promulgated, and proceedings thereon.</p><ol start="2"><li><p>Establish a database of registered and certified X-ray machines, which shall include but not be limited to the name of the owner or operator and the location of the machine.</p></li><li><p>Pursuant to its powers enumerated in § <a href="http://law.lis.virginia.gov/vacode/32.1-25/" target="_blank" rel="noreferrer">32.1-25</a>, provide for scheduled and random unannounced inspections of facilities and physicians&#39; offices that provide mammography services to ensure compliance with laws, regulations, or conditions specified by the Board.</p></li><li><p>Establish forms for the periodic Radiation Inspection Report.</p></li><li><p>Develop programs for responding adequately to radiation emergencies and coordinate such programs with the Department of Emergency Management.</p></li><li><p>Make available to the public a list of persons who are certified as professionals to offer screening, testing, or mitigation for radon pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-229.01/" target="_blank" rel="noreferrer">32.1-229.01</a>.</p></li><li><p>Publish and make available a list of qualified inspectors of X-rays and X-ray machines.</p></li></ol><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0041" target="_blank" rel="noreferrer">41</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0466" target="_blank" rel="noreferrer">466</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0298" target="_blank" rel="noreferrer">298</a>.</p>',9),r=[o];function s(l,d,c,p,g,h){return a(),i("div",null,r)}const u=e(n,[["render",s]]);export{m as __pageData,u as default};
