import{_ as a,o as e,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-23.5 Reporting of certain data regarding financial assistance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-23.5.md","filePath":"vacode/32.1-23.5.md"}'),i={name:"vacode/32.1-23.5.md"},r=n('<h1 id="§-32-1-23-5-reporting-of-certain-data-regarding-financial-assistance" tabindex="-1">§ 32.1-23.5 Reporting of certain data regarding financial assistance. <a class="header-anchor" href="#§-32-1-23-5-reporting-of-certain-data-regarding-financial-assistance" aria-label="Permalink to &quot;§ 32.1-23.5 Reporting of certain data regarding financial assistance.&quot;">​</a></h1><p>The Commissioner shall report annually by November 1 to the Chairmen of the House Committees on Appropriations and Health, Welfare and Institutions and the Senate Committees on Finance and Appropriations and Education and Health regarding data collected pursuant to subsection F of § <a href="/vacode/32.1-276.5/">32.1-276.5</a>, including the value of (i) the amount of charity care, discounted care, or other financial assistance provided by each hospital under its financial assistance policy that is required to be reported in accordance with subsection F of § <a href="/vacode/32.1-276.5/">32.1-276.5</a> and (ii) the amount of uncollected bad debt, including any uncollected bad debt from payment plans entered into in accordance with subsection C of § <a href="/vacode/32.1-137.09/">32.1-137.09</a>.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0679" target="_blank" rel="noreferrer">679</a>.</p>',3),o=[r];function c(s,d,l,f,p,h){return e(),t("div",null,o)}const u=a(i,[["render",c]]);export{g as __pageData,u as default};
