import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-241 Powers and duties of Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-241.md","filePath":"vacode/32.1-241.md"}'),a={name:"vacode/32.1-241.md"},n=i('<h1 id="§-32-1-241-powers-and-duties-of-board" tabindex="-1">§ 32.1-241 Powers and duties of Board. <a class="header-anchor" href="#§-32-1-241-powers-and-duties-of-board" aria-label="Permalink to &quot;§ 32.1-241 Powers and duties of Board.&quot;">​</a></h1><p>The Board shall:</p><ol><li><p>Advise the Governor, other state agencies, the federal government, and local governing bodies on matters pertaining to chemical exposures posing a threat to public health or the environment;</p></li><li><p>Collect from any source, necessary information concerning substances which are toxic in certain concentrations and under certain conditions;</p></li><li><p>Catalogue information on substances that are toxic so that the information can be retrieved quickly for use;</p></li><li><p>Institute proceedings in any appropriate court to compel the production of information concerning substances which are toxic;</p></li><li><p>Review and evaluate the information to be used in making a determination regarding toxicity of any substance and the concentrations and conditions under which the substance is toxic;</p></li><li><p>Disseminate information concerning toxic substances to other state agencies, political subdivisions of the Commonwealth, health professionals, the media, and the public by communicating the risk of chemical exposure through developing and disseminating documents, technical reports, information sheets, advisories, and press releases;</p></li><li><p>Investigate potential human health effects associated with environmental exposures through biomedical studies to address emergency and nonemergency site-specific problems;</p></li><li><p>Develop health risk assessments for specific chemical exposures via air, water, and food; coordinate assessments of such risks with other state agencies through the convening of assessment groups; and submit recommendations to prevent exposure of citizens to toxic substances, including, but not limited to, the closure of bodies of water and advisories relating to food consumption; and</p></li><li><p>Promulgate regulations to specify as necessary the details of the program.</p></li></ol><p>Code 1950, §§ 32-428.1, 32-429; 1976, c. 627; 1977, c. 471; 1979, c. 711; 1982, c. 16; 1992, c. 203; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0090" target="_blank" rel="noreferrer">90</a>.</p>',4),s=[n];function r(c,d,l,h,p,u){return t(),o("div",null,s)}const g=e(a,[["render",r]]);export{f as __pageData,g as default};
