import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-248.2 Use of rainwater and reuse of gray water; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-248.2.md","filePath":"vacode/32.1-248.2.md"}'),n={name:"vacode/32.1-248.2.md"},o=t('<h1 id="§-32-1-248-2-use-of-rainwater-and-reuse-of-gray-water-regulations" tabindex="-1">§ 32.1-248.2 Use of rainwater and reuse of gray water; regulations. <a class="header-anchor" href="#§-32-1-248-2-use-of-rainwater-and-reuse-of-gray-water-regulations" aria-label="Permalink to &quot;§ 32.1-248.2 Use of rainwater and reuse of gray water; regulations.&quot;">​</a></h1><p>A. The Board shall adopt regulations regarding the use of gray water and rainwater. The regulations shall (i) describe the conditions under which gray water and rainwater may appropriately be used and for what purposes; (ii) include categories of gray water, such as types of used household water and used water from businesses, that are appropriate for reuse; and (iii) include a definition of gray water that excludes used toilet water. The regulations shall also provide standards for the use of rainwater harvesting systems, including systems that collect rainwater for use by commercial enterprises but do not provide water for human consumption, as defined in § <a href="/vacode/32.1-167/">32.1-167</a>.</p><p>Such regulations shall not apply to water not for human consumption, as defined in § <a href="/vacode/32.1-167/">32.1-167</a>, including gray water and rainwater, that is produced and utilized by any facility that is permitted through a Virginia Pollutant Discharge Elimination System permit or General Virginia Pollution Abatement permit.</p><p>B. The Department, in conjunction with the Department of Environmental Quality, shall promote the use of rainwater and reuse of gray water as means to reduce fresh water consumption, ease demands on public treatment works and water supply systems, and promote conservation.</p><p>C. The Department, in conjunction with the Department of Environmental Quality, shall consider recognizing rainwater as an independent source of fresh water available for use by the residents of the Commonwealth.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0155" target="_blank" rel="noreferrer">155</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>.</p>',6),i=[o];function s(l,d,u,c,h,p){return a(),r("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
