import{_ as t,o as i,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 32.1-275.2 Notation on birth records of missing children.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-275.2.md","filePath":"vacode/32.1-275.2.md"}'),r={name:"vacode/32.1-275.2.md"},o=e("h1",{id:"§-32-1-275-2-notation-on-birth-records-of-missing-children",tabindex:"-1"},[n("§ 32.1-275.2 Notation on birth records of missing children. "),e("a",{class:"header-anchor",href:"#§-32-1-275-2-notation-on-birth-records-of-missing-children","aria-label":'Permalink to "§ 32.1-275.2 Notation on birth records of missing children."'},"​")],-1),s=e("p",null,"Upon receiving a report of the disappearance of any child born in this Commonwealth, the State Registrar shall indicate in a clear and conspicuous manner in the child's birth record that the child has been reported as missing, including the title and location of the law-enforcement agency providing the report. Upon receiving a request for any birth records containing a report of the disappearance of any child, the State Registrar shall immediately notify the local law-enforcement agency which provided the missing child report to the State Registrar. The State Registrar shall transmit any relevant information concerning the applicant's identity, address, and other pertinent data immediately to the relevant local law-enforcement agency. The State Registrar shall retain the original written request until notified of the missing child's recovery or the child attains the age of eighteen. Upon notification that any missing child has been recovered, the State Registrar shall remove the report of the disappearance from the child's birth record.",-1),c=e("p",null,"1990, c. 295.",-1),h=[o,s,c];function d(l,p,m,f,g,_){return i(),a("div",null,h)}const u=t(r,[["render",d]]);export{b as __pageData,u as default};
