import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse(`{"title":"§ 32.1-291.10 Manner of making, amending, or revoking anatomical gift of decedent's body or part.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-291.10.md","filePath":"vacode/32.1-291.10.md"}`),n={name:"vacode/32.1-291.10.md"},t=r('<h1 id="§-32-1-291-10-manner-of-making-amending-or-revoking-anatomical-gift-of-decedent-s-body-or-part" tabindex="-1">§ 32.1-291.10 Manner of making, amending, or revoking anatomical gift of decedent&#39;s body or part. <a class="header-anchor" href="#§-32-1-291-10-manner-of-making-amending-or-revoking-anatomical-gift-of-decedent-s-body-or-part" aria-label="Permalink to &quot;§ 32.1-291.10 Manner of making, amending, or revoking anatomical gift of decedent&#39;s body or part.&quot;">​</a></h1><p>A. A person authorized to make an anatomical gift under § <a href="http://law.lis.virginia.gov/vacode/32.1-291.9/" target="_blank" rel="noreferrer">32.1-291.9</a> may make an anatomical gift by a document of gift signed by the person making the gift or by that person&#39;s oral communication that is electronically recorded or is contemporaneously reduced to a record and signed by the individual receiving the oral communication.</p><p>B. Subject to subsection C, an anatomical gift by a person authorized under § <a href="http://law.lis.virginia.gov/vacode/32.1-291.9/" target="_blank" rel="noreferrer">32.1-291.9</a> may be amended or revoked orally or in a record by any member of a prior class who is reasonably available. If more than one member of the prior class is reasonably available, the gift made by a person authorized under § <a href="http://law.lis.virginia.gov/vacode/32.1-291.9/" target="_blank" rel="noreferrer">32.1-291.9</a> may be:</p><ol><li><p>Amended only if a majority of the reasonably available members agree to the amending of the gift; or</p></li><li><p>Revoked only if a majority of the reasonably available members agree to the revoking of the gift.</p></li></ol><p>C. A revocation under subsection B is effective only if, before an incision has been made to remove a part from the donor&#39;s body or before invasive procedures have begun to prepare the recipient, the procurement organization, transplant hospital, or physician or technician knows of the revocation.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>.</p>',6),i=[t];function l(d,c,s,f,m,g){return a(),o("div",null,i)}const b=e(n,[["render",l]]);export{h as __pageData,b as default};
