import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-291.12 Search and notification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-291.12.md","filePath":"vacode/32.1-291.12.md"}'),o={name:"vacode/32.1-291.12.md"},r=t('<h1 id="§-32-1-291-12-search-and-notification" tabindex="-1">§ 32.1-291.12 Search and notification. <a class="header-anchor" href="#§-32-1-291-12-search-and-notification" aria-label="Permalink to &quot;§ 32.1-291.12 Search and notification.&quot;">​</a></h1><p>A. The following persons shall make a reasonable search of an individual who the person reasonably believes is dead or whose death is imminent for a document of gift or other information identifying the individual as a donor or as an individual who made a refusal:</p><ol><li><p>A law-enforcement officer, a firefighter, emergency medical services personnel, or other emergency rescuer finding the individual; and</p></li><li><p>If no other source of the information is immediately available, a hospital, as soon as practical after the individual&#39;s arrival at the hospital.</p></li></ol><p>B. If a document of gift or a refusal to make an anatomical gift is located by the search required by subdivision A 1 and the individual or deceased individual to whom it relates is taken to a hospital, the person responsible for conducting the search shall send the document of gift or refusal to the hospital.</p><p>C. A person is not subject to criminal or civil liability for failing to discharge the duties imposed by this section but may be subject to administrative sanctions.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',6),n=[r];function s(l,c,d,h,f,p){return a(),i("div",null,n)}const u=e(o,[["render",s]]);export{_ as __pageData,u as default};
