import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-291.16 Sale or purchase of parts prohibited; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-291.16.md","filePath":"vacode/32.1-291.16.md"}'),i={name:"vacode/32.1-291.16.md"},n=a('<h1 id="§-32-1-291-16-sale-or-purchase-of-parts-prohibited-penalty" tabindex="-1">§ 32.1-291.16 Sale or purchase of parts prohibited; penalty. <a class="header-anchor" href="#§-32-1-291-16-sale-or-purchase-of-parts-prohibited-penalty" aria-label="Permalink to &quot;§ 32.1-291.16 Sale or purchase of parts prohibited; penalty.&quot;">​</a></h1><p>A. With the exception of hair, ova, blood, and other self-replicating body fluids, it shall be unlawful for any person to sell, to offer to sell, to buy, to offer to buy, or to procure through purchase any natural body part for any reason including, but not limited to, medical and scientific uses such as transplantation, implantation, infusion, or injection. Any person engaging in any of these prohibited activities shall be guilty of a Class 4 felony.</p><p>B. Nothing in this section shall prohibit the reimbursement of reasonable expenses associated with the removal, processing, preservation, quality control, storage, transportation, implantation, or disposal of a part.</p><p>C. This section shall not be construed to prohibit the donation of any organs, tissues, or any natural body part, knowing that the donor is, or was, infected with a sexually transmitted infection, for use in medical or scientific research.</p><p>D. Notwithstanding the provisions of subsection A, this section shall not prohibit the donation or acquisition of organs for transplantation, provided that (i) the recipient of such organ is informed that such organ is infected with human immunodeficiency virus and, following such notice, consents to the receipt of such organ and (ii) acquisition and transplantation of such organ is in compliance with the provisions of the federal HIV Organ Policy Equity Act, 42 U.S.C. § 274f-5.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0465" target="_blank" rel="noreferrer">465</a>.</p>',6),r=[n];function s(l,c,p,h,f,d){return t(),o("div",null,r)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
