import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-291.19 Law governing validity; choice of law as to execution of document of gift; presumption of validity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-291.19.md","filePath":"vacode/32.1-291.19.md"}'),a={name:"vacode/32.1-291.19.md"},n=i('<h1 id="§-32-1-291-19-law-governing-validity-choice-of-law-as-to-execution-of-document-of-gift-presumption-of-validity" tabindex="-1">§ 32.1-291.19 Law governing validity; choice of law as to execution of document of gift; presumption of validity. <a class="header-anchor" href="#§-32-1-291-19-law-governing-validity-choice-of-law-as-to-execution-of-document-of-gift-presumption-of-validity" aria-label="Permalink to &quot;§ 32.1-291.19 Law governing validity; choice of law as to execution of document of gift; presumption of validity.&quot;">​</a></h1><p>A. A document of gift is valid if executed in accordance with:</p><ol><li><p>This Act;</p></li><li><p>The laws of the state or country where it was executed; or</p></li><li><p>The laws of the state or country where the person making the anatomical gift was domiciled, has a place of residence, or was a national at the time the document of gift was executed.</p></li></ol><p>B. If a document of gift is valid under this section, the law of this state governs the interpretation of the document of gift.</p><p>C. A person may presume that a document of gift or amendment of an anatomical gift is valid unless that person knows that it was not validly executed or was revoked.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>.</p>',6),r=[n];function c(s,l,f,d,p,h){return t(),o("div",null,r)}const m=e(a,[["render",c]]);export{g as __pageData,m as default};
