import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 32.1-291.6 Amending or revoking anatomical gift before donor's death.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-291.6.md","filePath":"vacode/32.1-291.6.md"}`),r={name:"vacode/32.1-291.6.md"},i=a('<h1 id="§-32-1-291-6-amending-or-revoking-anatomical-gift-before-donor-s-death" tabindex="-1">§ 32.1-291.6 Amending or revoking anatomical gift before donor&#39;s death. <a class="header-anchor" href="#§-32-1-291-6-amending-or-revoking-anatomical-gift-before-donor-s-death" aria-label="Permalink to &quot;§ 32.1-291.6 Amending or revoking anatomical gift before donor&#39;s death.&quot;">​</a></h1><p>A. Subject to § <a href="http://law.lis.virginia.gov/vacode/32.1-291.8/" target="_blank" rel="noreferrer">32.1-291.8</a>, a donor or other person authorized to make an anatomical gift under § <a href="http://law.lis.virginia.gov/vacode/32.1-291.4/" target="_blank" rel="noreferrer">32.1-291.4</a> may amend or revoke an anatomical gift by:</p><ol><li>A record signed by:</li></ol><p>a. The donor;</p><p>b. The other person; or</p><p>c. Subject to subsection B, another individual acting at the direction of the donor or the other person if the donor or other person is physically unable to sign; or</p><ol start="2"><li>Later-executed document of gift that amends or revokes a previous anatomical gift or portion of an anatomical gift, either expressly or by inconsistency.</li></ol><p>B. A record signed pursuant to subdivision A 1 c shall:</p><ol><li><p>Be witnessed by at least two adults, at least one of whom is a disinterested witness, who have signed at the request of the donor or the other person; and</p></li><li><p>State that it has been signed and witnessed as provided in subdivision 1.</p></li></ol><p>C. Subject to § <a href="http://law.lis.virginia.gov/vacode/32.1-291.8/" target="_blank" rel="noreferrer">32.1-291.8</a>, a donor or other person authorized to make an anatomical gift under § <a href="http://law.lis.virginia.gov/vacode/32.1-291.4/" target="_blank" rel="noreferrer">32.1-291.4</a> may revoke an anatomical gift by the destruction or cancellation of the document of gift, or the portion of the document of gift used to make the gift, with the intent to revoke the gift.</p><p>D. A donor may amend or revoke an anatomical gift that was not made in a will by any form of communication during a terminal illness or injury addressed to at least two adults, at least one of whom is a disinterested witness.</p><p>E. A donor who makes an anatomical gift in a will may amend or revoke the gift in the manner provided for amendment or revocation of wills or as provided in subsection A.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>.</p>',13),n=[i];function s(d,l,c,h,f,g){return o(),t("div",null,n)}const v=e(r,[["render",s]]);export{m as __pageData,v as default};
