import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-309.3 Cremations and burials at sea.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-309.3.md","filePath":"vacode/32.1-309.3.md"}'),i={name:"vacode/32.1-309.3.md"},n=r('<h1 id="§-32-1-309-3-cremations-and-burials-at-sea" tabindex="-1">§ 32.1-309.3 Cremations and burials at sea. <a class="header-anchor" href="#§-32-1-309-3-cremations-and-burials-at-sea" aria-label="Permalink to &quot;§ 32.1-309.3 Cremations and burials at sea.&quot;">​</a></h1><p>No dead human body whose death occurred in Virginia shall be cremated or buried at sea, irrespective of the cause and manner of death, unless the Chief Medical Examiner, an Assistant Chief Medical Examiner, or a medical examiner appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-282/" target="_blank" rel="noreferrer">32.1-282</a> has determined that there is no further need for medicolegal inquiry into the death and so certifies upon a form supplied by the Office of the Chief Medical Examiner. For this service the Chief Medical Examiner, an Assistant Chief Medical Examiner, or a medical examiner appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-282/" target="_blank" rel="noreferrer">32.1-282</a> shall be entitled to a fee established by the Board, not to exceed the fee provided for in subsection D of § <a href="http://law.lis.virginia.gov/vacode/32.1-283/" target="_blank" rel="noreferrer">32.1-283</a>, to be paid by the applicant for the certificate.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0228" target="_blank" rel="noreferrer">228</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0583" target="_blank" rel="noreferrer">583</a>.</p>',3),o=[n];function s(d,l,c,h,f,p){return a(),t("div",null,o)}const u=e(i,[["render",s]]);export{m as __pageData,u as default};
