import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 32.1-309.5 Storage of a dead human body.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-309.5.md","filePath":"vacode/32.1-309.5.md"}'),o={name:"vacode/32.1-309.5.md"},n=r('<h1 id="§-32-1-309-5-storage-of-a-dead-human-body" tabindex="-1">§ 32.1-309.5 Storage of a dead human body. <a class="header-anchor" href="#§-32-1-309-5-storage-of-a-dead-human-body" aria-label="Permalink to &quot;§ 32.1-309.5 Storage of a dead human body.&quot;">​</a></h1><p>If a dead human body is to be stored for more than 48 hours prior to disposition, any institution having custody of such body shall ensure that the dead human body is maintained in refrigeration at no more than approximately 40 degrees Fahrenheit or shall enter into an agreement with a local funeral service establishment pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/32.1-309.2/" target="_blank" rel="noreferrer">32.1-309.2</a>. Any related expenses shall be borne by the claimant or the relevant city or county in accordance with § <a href="http://law.lis.virginia.gov/vacode/32.1-309.1/" target="_blank" rel="noreferrer">32.1-309.1</a> or <a href="http://law.lis.virginia.gov/vacode/32.1-309.2/" target="_blank" rel="noreferrer">32.1-309.2</a>.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>.</p>',3),i=[n];function d(s,l,h,c,_,f){return a(),t("div",null,i)}const g=e(o,[["render",d]]);export{u as __pageData,g as default};
