import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 32.1-321.3 Fraudulently obtaining benefits; liability for fraudulently issued benefits; civil action to recover; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-321.3.md","filePath":"vacode/32.1-321.3.md"}'),n={name:"vacode/32.1-321.3.md"},o=a('<h1 id="§-32-1-321-3-fraudulently-obtaining-benefits-liability-for-fraudulently-issued-benefits-civil-action-to-recover-penalty" tabindex="-1">§ 32.1-321.3 Fraudulently obtaining benefits; liability for fraudulently issued benefits; civil action to recover; penalty. <a class="header-anchor" href="#§-32-1-321-3-fraudulently-obtaining-benefits-liability-for-fraudulently-issued-benefits-civil-action-to-recover-penalty" aria-label="Permalink to &quot;§ 32.1-321.3 Fraudulently obtaining benefits; liability for fraudulently issued benefits; civil action to recover; penalty.&quot;">​</a></h1><p>Any person who, on behalf of himself or another, issues, obtains or attempts to obtain medical assistance benefits by means of (i) knowingly and willfully making or causing to be made any false statement or false representation of material fact; (ii) knowingly and willfully concealing or causing to be concealed a material fact; or (iii) engaging in any other fraudulent scheme or device shall be liable for repayment of the cost of all benefits issued as a result of such fraud, plus interest on the amount of the benefits issued at the rate of 1.5 percent per month for the period from the date upon which payment was made for such benefits to the date on which repayment is made to the Commonwealth.</p><p>Such matters may be referred for criminal action to the attorney for the Commonwealth having jurisdiction over the case. The Attorney General may, independent of any referral to or decision of the attorney for the Commonwealth, petition the circuit court in the jurisdiction of the alleged offense to seek an order assessing civil penalties in the amount of the benefits issued, in addition to repayment and interest and any other penalties provided by law.</p><p>All civil penalties shall be deposited in the general fund of the state treasury upon receipt.</p><p>1986, c. 551; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0991" target="_blank" rel="noreferrer">991</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',5),r=[o];function l(s,f,c,d,u,h){return t(),i("div",null,r)}const y=e(n,[["render",l]]);export{b as __pageData,y as default};
