import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 32.1-330.1 Department to implement premium assistance program for HIV-positive individuals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-330.1.md","filePath":"vacode/32.1-330.1.md"}'),o={name:"vacode/32.1-330.1.md"},r=t('<h1 id="§-32-1-330-1-department-to-implement-premium-assistance-program-for-hiv-positive-individuals" tabindex="-1">§ 32.1-330.1 Department to implement premium assistance program for HIV-positive individuals. <a class="header-anchor" href="#§-32-1-330-1-department-to-implement-premium-assistance-program-for-hiv-positive-individuals" aria-label="Permalink to &quot;§ 32.1-330.1 Department to implement premium assistance program for HIV-positive individuals.&quot;">​</a></h1><p>The Board of Medical Assistance Services shall from funds eligible for this purpose from Title II of the Ryan White Comprehensive AIDS Resources Emergency CARE Act (42 U.S.C. § 300ff-21 et seq.) or other funds appropriated or made available for this purpose, implement, and may promulgate any necessary regulations for implementation of, a premium assistance program for HIV-positive individuals which shall have, at minimum, the following characteristics:</p><ol><li><p>Payment of health insurance premiums for individuals who are not eligible for Medicaid and who can document (i) HIV infection and inability to continue working for medical reasons and (ii) eligibility to continue their employer&#39;s group policy pursuant to the Consolidated Omnibus Budget Reconciliation Act of 1985;</p></li><li><p>Financial eligibility criteria allowing a maximum income of no more than 250 percent of the federal poverty guidelines and countable liquid assets of no more than $10,000 in value;</p></li><li><p>Funds eligible under Title II of the Ryan White CARE Act shall not be used toward copayments and deductible payments; and</p></li><li><p>Coverage of family members, if the HIV-infected person&#39;s policy is the sole source of health insurance.</p></li></ol><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0195" target="_blank" rel="noreferrer">195</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0870" target="_blank" rel="noreferrer">870</a>.</p>',4),n=[r];function s(l,p,m,c,d,f){return i(),a("div",null,n)}const g=e(o,[["render",s]]);export{h as __pageData,g as default};
