import{_ as e,o as a,c as s,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-35 List and reports of diseases and dangerous microbes and pathogens.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-35.md","filePath":"vacode/32.1-35.md"}'),r={name:"vacode/32.1-35.md"},o=t('<h1 id="§-32-1-35-list-and-reports-of-diseases-and-dangerous-microbes-and-pathogens" tabindex="-1">§ 32.1-35 List and reports of diseases and dangerous microbes and pathogens. <a class="header-anchor" href="#§-32-1-35-list-and-reports-of-diseases-and-dangerous-microbes-and-pathogens" aria-label="Permalink to &quot;§ 32.1-35 List and reports of diseases and dangerous microbes and pathogens.&quot;">​</a></h1><p>The Board shall promulgate from time to time a list of diseases, including diseases caused by exposure to any toxic substance as defined in § <a href="http://law.lis.virginia.gov/vacode/32.1-239/" target="_blank" rel="noreferrer">32.1-239</a> and including diseases that may be caused by exposure to an agent or substance that has the potential for use as a weapon, that shall be required to be reported. The Board shall also promulgate from time to time a list of dangerous microbes and pathogens that shall be required to be reported by laboratories. The Board may classify such diseases, microbes and pathogens and prescribe the manner and time of such reporting.</p><p>Code 1950, § 32-16; 1979, c. 711; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0100" target="_blank" rel="noreferrer">100</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0768" target="_blank" rel="noreferrer">768</a>.</p>',3),n=[o];function i(d,c,l,p,h,g){return a(),s("div",null,n)}const u=e(r,[["render",i]]);export{f as __pageData,u as default};
