import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-354 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-354.md","filePath":"vacode/32.1-354.md"}'),n={name:"vacode/32.1-354.md"},r=o('<h1 id="§-32-1-354-definitions" tabindex="-1">§ 32.1-354 Definitions. <a class="header-anchor" href="#§-32-1-354-definitions" aria-label="Permalink to &quot;§ 32.1-354 Definitions.&quot;">​</a></h1><p>As used in this chapter, unless the context clearly indicates otherwise:</p><p>&quot;Agreement&quot; means the agreement or agreements between the Commonwealth, as seller of the Tobacco Assets, and the Corporation, as purchaser of the Tobacco Assets. The sale by the Commonwealth of the Tobacco Assets pursuant to any such agreement shall be a true sale and not a borrowing.</p><p>&quot;Board&quot; means the Board of Trustees of the Foundation appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-357/" target="_blank" rel="noreferrer">32.1-357</a>.</p><p>&quot;Corporation&quot; means the Tobacco Settlement Financing Corporation as created under state law.</p><p>&quot;Director&quot; means the director of the Foundation appointed pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-358/" target="_blank" rel="noreferrer">32.1-358</a>.</p><p>&quot;Endowment&quot; means the Virginia Tobacco Settlement Foundation Endowment established pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-361.1/" target="_blank" rel="noreferrer">32.1-361.1</a>.</p><p>&quot;Foundation&quot; means the Virginia Foundation for Healthy Youth, created pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-355/" target="_blank" rel="noreferrer">32.1-355</a>.</p><p>&quot;Foundation Allocation&quot; means 10 percent of the annual amount received under the Master Settlement Agreement by the Commonwealth, or that would have been received but for the sale of such allocation pursuant to an agreement, between the starting and ending dates specified in the agreement.</p><p>&quot;Fund&quot; means the Virginia Tobacco Settlement Fund established pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-360/" target="_blank" rel="noreferrer">32.1-360</a>.</p><p>&quot;Period of sale&quot; means the time during which a purchase under an agreement is entitled to receive the Foundation Allocation.</p><p>&quot;Tobacco Assets&quot; means all right, title, and interest in and to the portion of the Foundation Allocation that may be sold to the Corporation from time to time.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0962" target="_blank" rel="noreferrer">962</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0424" target="_blank" rel="noreferrer">424</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>.</p>',13),i=[r];function s(l,h,c,u,p,d){return t(),a("div",null,i)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
