import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-355 Virginia Foundation for Healthy Youth created; purposes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-355.md","filePath":"vacode/32.1-355.md"}'),o={name:"vacode/32.1-355.md"},n=t('<h1 id="§-32-1-355-virginia-foundation-for-healthy-youth-created-purposes" tabindex="-1">§ 32.1-355 Virginia Foundation for Healthy Youth created; purposes. <a class="header-anchor" href="#§-32-1-355-virginia-foundation-for-healthy-youth-created-purposes" aria-label="Permalink to &quot;§ 32.1-355 Virginia Foundation for Healthy Youth created; purposes.&quot;">​</a></h1><p>A. The Virginia Foundation for Healthy Youth (VFHY) is hereby created as a body corporate and a political subdivision of the Commonwealth and as such shall have, and is hereby vested with, all of the politic and corporate powers as are set forth in this chapter. The Foundation is established for the purposes of determining the appropriate recipients of moneys in the Virginia Tobacco Settlement Fund and causing distribution of such moneys for the purposes provided in this chapter.</p><p>B. The Foundation shall include the following divisions:</p><ol><li><p>The Virginia Tobacco Settlement Foundation (VTSF) division, to assist in financing efforts to restrict the use of tobacco products by minors through such means as educational and awareness programs on the health effects of tobacco use on minors and enforcement of laws restricting the distribution of tobacco products to minors;</p></li><li><p>The Virginia Youth Obesity Prevention (VYOP) division, which may use moneys from the Fund to assist in financing efforts to reduce childhood obesity through such means as educational and awareness programs, implementing evidence-based practices, and assisting schools and communities with policies and programs; and</p></li><li><p>The Virginia Youth Substance Use Prevention (VYSUP) division, which may use moneys from the Fund to assist in financing efforts to prevent and reduce substance use by youth in the Commonwealth through such means as educational and awareness programs, implementing evidence-based practices, and assisting schools and communities with policies and programs.</p></li></ol><p>C. The Foundation shall have only those powers enumerated in § <a href="http://law.lis.virginia.gov/vacode/32.1-356/" target="_blank" rel="noreferrer">32.1-356</a>.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0962" target="_blank" rel="noreferrer">962</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0424" target="_blank" rel="noreferrer">424</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0060" target="_blank" rel="noreferrer">60</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0109" target="_blank" rel="noreferrer">109</a>.</p>',6),r=[n];function s(h,c,l,d,p,u){return i(),a("div",null,r)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
