import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 32.1-37.01 (Effective March 1, 2021) Posting of information about cases of communicable disease of public health threat.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-37.01.md","filePath":"vacode/32.1-37.01.md"}'),i={name:"vacode/32.1-37.01.md"},r=o('<h1 id="§-32-1-37-01-effective-march-1-2021-posting-of-information-about-cases-of-communicable-disease-of-public-health-threat" tabindex="-1">§ 32.1-37.01 (Effective March 1, 2021) Posting of information about cases of communicable disease of public health threat. <a class="header-anchor" href="#§-32-1-37-01-effective-march-1-2021-posting-of-information-about-cases-of-communicable-disease-of-public-health-threat" aria-label="Permalink to &quot;§ 32.1-37.01 (Effective March 1, 2021) Posting of information about cases of communicable disease of public health threat.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Reporting entity&quot; means a medical care facility, residential or day program, service or facility licensed or operated by any agency of the Commonwealth, school, or summer camp required to report an outbreak of a communicable disease pursuant to § <a href="/vacode/32.1-37/">32.1-37</a>.</p><p>B. Upon declaration of an emergency by the Governor pursuant to § <a href="/vacode/44-146.17/">44-146.17</a> in response to a communicable disease of public health threat, the Department shall make information regarding outbreaks of such communicable disease of public health threat reported pursuant to § <a href="/vacode/32.1-37/">32.1-37</a> available to the public on a website maintained by the Department, provided the release of such information does not violate the provisions of § <a href="/vacode/32.1-41/">32.1-41</a>. Such information shall include (i) the name of the reporting entity at which an outbreak of such communicable disease of public health threat has been reported; (ii) the number of confirmed cases of such communicable disease of public health threat reported by such reporting entity; and (iii) the number of deaths resulting from such communicable disease of public health threat reported by such reporting entity.</p><p>2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0012" target="_blank" rel="noreferrer">12</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0024" target="_blank" rel="noreferrer">24</a>.</p>',5),n=[r];function c(s,h,l,f,u,m){return a(),t("div",null,n)}const b=e(i,[["render",c]]);export{d as __pageData,b as default};
